mapper.select:
select rc.document_id as id, a.title as title, a.create_time as createTime
from read_count as rc
         left join article as a on rc.document_id = a.id
where rc.document_type = ${@com.ketan.api.model.enums.DocumentTypeEnum@ARTICLE.code}
  and a.status = ${@com.ketan.api.model.enums.PushStatusEnum@ONLINE.code}
  and a.deleted = ${@com.ketan.api.model.enums.YesOrNoEnum@NO.code}
order by rc.cnt desc

select DATE_FORMAT(create_time, "%Y") as year, count(*) as articleCount
from article
where user_id = #{userId}
  and status = ${@com.ketan.api.model.enums.PushStatusEnum@ONLINE.code}
  and deleted = ${@com.ketan.api.model.enums.YesOrNoEnum@NO.code}
group by year

select rc.document_id as id, a.title as title, a.create_time as createTime
from read_count as rc left join article as a on rc.document_id = a.id
where rc.document_type = ${@com.ketan.api.model.enums.DocumentTypeEnum@ARTICLE.code}
and a.status = ${@com.ketan.api.model.enums.PushStatusEnum@ONLINE.code}
and a.deleted = ${@com.ketan.api.model.enums.YesOrNoEnum@NO.code} and a.user_id = #{userId} order
by rc.cnt desc
